import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    private final Shouty shouty = new Shouty();

    @Given("^(.+) is at (\\d+), (\\d+)$")
    public void personIsAt(String person, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(person, new Coordinate(xCoord, yCoord));
    }

    @When("^(.+) shouts$")
    public void personShouts(String person) throws Throwable {
        shouty.shout(person, ARBITRARY_MESSAGE);
    }

    @Then("^(.+) should hear (.+)$")
    public void personShouldHearAnotherPerson(String listener, String shouter) throws Throwable {
        Assert.assertTrue(shouty.getShoutsHeardBy(listener).containsKey(shouter));
    }

    @Then("^(.+) should not hear (.+)$")
    public void personShouldNotHearAnotherPerson(String listener, String shouter) throws Throwable {
        Assert.assertFalse(shouty.getShoutsHeardBy(listener).containsKey(shouter));
    }







}

