import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    private final Shouty shouty = new Shouty();

    @Given("^(.+) is at (\\d+), (\\d+)$")
    public void lucyIsAt(String name, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(name, new Coordinate(xCoord, yCoord));
    }

    @When("^(.+) shouts$")
    public void seanShouts(String name) throws Throwable {
        shouty.shout(name, ARBITRARY_MESSAGE);
    }

    @Then("^(.+) should hear (.+)$")
    public void lucyShouldHearHim(String listener, String shouter) throws Throwable {
        if("nothing".equals(shouter)) {
            Assert.assertEquals(0, shouty.getShoutsHeardBy(listener).size());
        } else {
            Assert.assertEquals(1, shouty.getShoutsHeardBy(listener).size());
            Assert.assertTrue(shouty.getShoutsHeardBy(listener).containsKey(shouter));
        }
    }

    @Then("^(.+) should not hear (.+)$") 
    public void lucyShouldNotHearOscar(String listener, String shouter) throws Throwable {
        Assert.assertFalse(shouty.getShoutsHeardBy(listener).containsKey(shouter));
    }
}

