import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    private final Shouty shouty = new Shouty();

    @Given("^(Sean|Lucy|Oscar) is at (\\d+), (\\d+)$")
    public void personIsAt(String person, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(person, new Coordinate(xCoord, yCoord));
    }

    @When("^(Sean|Lucy|Oscar) shouts$")
    public void personShouts(String person) throws Throwable {
        shouty.shout(person, ARBITRARY_MESSAGE);
    }

    @Then("^Lucy should hear him$")
    public void lucyShouldHearHim() throws Throwable {
        Assert.assertEquals(1, shouty.getShoutsHeardBy("Lucy").size());
    }

    @Then("^Lucy should hear nothing$")
    public void lucy_should_hear_nothing() throws Throwable {
        Assert.assertEquals(0, shouty.getShoutsHeardBy("Lucy").size());
    }


    @Then("^([A-Z][a-z]*) should not hear ([A-Z][a-z]*)$")
    public void userShouldNotHearShouter(String user, String shouter) throws Throwable {
        Assert.assertFalse(shouty.getShoutsHeardBy(user).containsKey(shouter)); 
    }

    @Then("^([A-Z][a-z]*) should hear ([A-Z][a-z]*)$")
    public void userShouldHearShouter(String user, String shouter) throws Throwable {
        Assert.assertTrue(shouty.getShoutsHeardBy(user).containsKey(shouter));
    }

}

