import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import org.springframework.beans.factory.annotation.*;
import java.util.List;
import javax.xml.transform.Transformer;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";

    private final ShoutyHelper shoutyHelper = new ShoutyHelper();

    @When("^([A-Z][a-z]*) shouts$")
    public void shouterShouts(String shouter) throws Throwable {
        shoutyHelper.shout(shouter, ARBITRARY_MESSAGE);
    }

    @Then("^([A-Z][a-z]*) should hear him$")
    public void userShouldHearHim(String user) throws Throwable {
        Assert.assertEquals(1, shoutyHelper.getShoutsHeardBy(user).size());
    }

    @Then("^([A-Z][a-z]*) should hear nothing$")
    public void userShouldHearNothing(String user) throws Throwable {
        Assert.assertEquals(0, shoutyHelper.getShoutsHeardBy(user).size());
    }

    @Then("^([A-Z][a-z]*) should not hear ([A-Z][a-z]*)$")
    public void userShouldNotHearShouter(String user, String shouter) throws Throwable {
        Assert.assertFalse(shoutyHelper.getShoutsHeardBy(user).containsKey(shouter)); 
    }

    @Then("^([A-Z][a-z]*) should hear ([A-Z][a-z]*)$")
    public void userShouldHearShouter(String user, String shouter) throws Throwable {
        Assert.assertTrue(shoutyHelper.getShoutsHeardBy(user).containsKey(shouter));
    }

    @Then("^Lucy should hear (\\d+) shouts from Sean$")
    public void lucyShouldHearShoutsFromSean(int numShouts) throws Throwable {
        Assert.assertTrue(shoutyHelper.getShoutsHeardBy("Lucy").containsKey("Sean"));
        Assert.assertEquals(numShouts, shoutyHelper.getShoutsHeardBy("Lucy").get("Sean").size());
    }

    @Given("^([A-Z][a-z]*) is at (.*)$")
    public void personIsAt(String person, @Transform(CoordinateConverter.class) Coordinate coordinate) throws Throwable {
        shoutyHelper.setLocation(person, coordinate);
    }

    @Given("^people are located at$")
    public void peopleAreLocatedAt(List<PersonLocation> personLocations) throws Throwable {
        for (PersonLocation personLocation : personLocations) {
            shoutyHelper.setLocation(personLocation.name, new Coordinate(personLocation.x, personLocation.y));
        }
    }

}

Shouty.java:
import java.util.*;

public class Shouty {
  private static final int MESSAGE_RANGE = 1000;
  private Map<String, Coordinate> locations = new HashMap<String, Coordinate>();
  private Map<String, List<String> > shouts = new HashMap<String, List<String> >();

  public void setLocation(String person, Coordinate location) {
      locations.put(person, location);
  }

  public void shout(String shouter, String shout) {
      if (!shouts.containsKey(shouter)) {
          List<String> personsShouts = new ArrayList<String>();
          shouts.put(shouter, personsShouts);
      }

      shouts.get(shouter).add(shout);
  }

  public Map<String, List<String> > getShoutsHeardBy(String listener) {
      HashMap<String, List<String> > shoutsHeard = new HashMap<String, List<String> >();

      for (Map.Entry<String, List<String> > entry : shouts.entrySet()) {
          String shouter = entry.getKey();
          if (shouter.equals(listener)) {
              continue;
          }
          List<String> personsShouts = entry.getValue();
          int distance = locations.get(listener).distanceFrom(locations.get(shouter));
          if (distance < MESSAGE_RANGE)
              shoutsHeard.put(shouter, personsShouts);
      }

      return shoutsHeard;
  }
}

