public class Coordinate {
  private int x;
  private int y;

  public Coordinate(int xCoord, int yCoord){
    x = xCoord;
    y = yCoord;
  }

  public int distanceFrom(Coordinate other){
    int xSide = (x - other.x);
    int ySide = (y - other.y);
    return (int)Math.hypot(xSide, ySide);
  }
}

