import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import java.util.Map;
import java.util.List;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;




public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    @Autowired
    private ShoutyHelper shoutyHelper;

    @When("^(\\w+) shouts$")
    public void shouterShouts(String shouter) throws Throwable {
        shoutyHelper.shouty.shout(shouter, ARBITRARY_MESSAGE);
    }

    @Then("^(\\w+) should not hear (\\w+)$")
    public void listenerShouldNotHearShouter(String listener, String shouter) throws Throwable {
        Map<String, List<String> > shoutHeard = shoutyHelper.shouty.getShoutsHeardBy(listener);
        boolean hasHeard =  shoutHeard.containsKey(shouter);

        Assert.assertFalse(hasHeard );
    }

    @Then("^(\\w+) should hear (\\w+)$")
    public void listenerShouldHearShouter(String listener, String shouter) throws Throwable {       
       Assert.assertTrue(shoutyHelper.shouty.getShoutsHeardBy(listener).containsKey(shouter) );
    }

    @Then("^(\\w+) should hear (\\d+) shouts from (\\w+)$")
    public void listenerShouldHearNumberShouter(String listener,int numberShouts, String shouter) throws Throwable {
        Map<String, List<String> > shoutHeard = shoutyHelper.shouty.getShoutsHeardBy(listener);
        List<String> shouts = shoutHeard.get(shouter);

        Assert.assertEquals(numberShouts, shouts.size() );
    }

}

