import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import java.util.*;
import org.springframework.beans.factory.annotation.Autowired;


public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";

    @Autowired
    private final ShoutyHelper shouty = new ShoutyHelper();


    @When("^(\\w+) shouts$")
    public void shouterShouts(String shouter) throws Throwable {
        shouty.shout(shouter, ARBITRARY_MESSAGE);
    }

    @Then("^(\\w+) should not hear (\\w+)$")
    public void listenerShouldNotHearShouter(String listener, String shouter) throws Throwable {
        Map<String, List<String>> heardMap = shouty.getShoutsHeardBy(listener);
        Assert.assertFalse(heardMap.containsKey(shouter));
    }

    @Then("^(\\w+) should hear (\\w+)$")
    public void listenerShouldHearShouter(String listener, String shouter) throws Throwable {
        Map<String, List<String>> heardMap = shouty.getShoutsHeardBy(listener);
        Assert.assertTrue(heardMap.containsKey(shouter));
    }


    @Then("^(\\w+) should hear (\\d+) shouts from (\\w+)$")
    public void listenerShouldHearXShoutsFromShouter(String listener, int numShouts, String shouter) throws Throwable {
        Map<String, List<String>> heardMap = shouty.getShoutsHeardBy(listener);

        Assert.assertEquals(numShouts, heardMap.get(shouter).size());

    }




}

