public class Coordinate {
  private int x;
  private int y;

  public Coordinate(int xCoord, int yCoord){
    x = xCoord;
    y = yCoord;
  }

  public int distanceFrom(Coordinate other){
    int a = Math.abs(x - other.x);
    int b = Math.abs(y - other.y);

    return (int) Math.sqrt(Math.pow(a, 2) + Math.pow(b, 2)); 

  }
}

