import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import java.util.*;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    private final Shouty shouty = new Shouty();

    @Given("^(\\w+) is at (\\d+), (\\d+)$")
    public void shouterIsAt(String name, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(name, new Coordinate(xCoord, yCoord));
    }

    @When("^(\\w+) shouts$")
    public void shouterShouts(String shouter) throws Throwable {
        shouty.shout(shouter, ARBITRARY_MESSAGE);
    }


    @Then("^Lucy should not hear any shouts$")
    public void lucy_should_hear_nothing() throws Throwable {
        Assert.assertEquals(0, shouty.getShoutsHeardBy("Lucy").size());
    }

    @Then("^(\\w+) should not hear (\\w+)$")
    public void listenerShouldNotHearShouter(String listener, String shouter) throws Throwable {
        Map<String, List<String>> heardMap = shouty.getShoutsHeardBy(listener);
        Assert.assertFalse(heardMap.containsKey(shouter));
    }

    @Then("^(\\w+) should hear (\\w+)$")
    public void lucyShouldHearSean(String listener, String shouter) throws Throwable {
        Map<String, List<String>> heardMap = shouty.getShoutsHeardBy(listener);
        Assert.assertTrue(heardMap.containsKey(shouter));
    }


    @Given("^(\\w+) is the shouter$")
    public void lucyIsTheShouter(String name) throws Throwable {
        shouty.setLocation(name, new Coordinate(0, 0));
    }



}

