import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";

    private final Shouty shouty = new Shouty();

    @Given("^(Lucy|Sean|Oscar) is at (\\d+), (\\d+)$")
    public void seanIsAt(String name, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(name, new Coordinate(xCoord, yCoord));
    }

    @When("^(Sean|Oscar) shouts$")
    public void seanShouts(String name) throws Throwable {
        shouty.shout(name, ARBITRARY_MESSAGE);
    }


    @Then("^(Lucy) should hear ^(Sean)$")    
    public void lucyShouldHearHim(String name, String shouter) throws Throwable {
        Assert.assertEquals(1, shouty.getShoutsHeardBy(name).size());
    }


    @Then("^(Lucy) should not hear ^(Oscar|Sean)$")
    public void lucyShouldNotHearOscar(String name, String shouter) throws Throwable {
        System.out.println(shouty.getShoutsHeardBy(name).size());

        Assert.assertEquals(0, shouty.getShoutsHeardBy(name).size());
    }

}

