public class Coordinate {
  private int x;
  private int y;

  public Coordinate(int xCoord, int yCoord){
    x = xCoord;
    y = yCoord;
  }

  public int distanceFrom(Coordinate other){
      //Get absolute x-axis distance 
      int xDistance= Math.abs(x - other.x);
      //Get absolute y-axis distance
      int yDistance=Math.abs(y - other.y);
      //Get hypotenuse distance
      double hypotenuseDistance = (Math.sqrt(xDistance*xDistance + yDistance*yDistance));
      //Get absolute distance
      int finalDistance = (int) Math.abs(hypotenuseDistance);

   return finalDistance;
  }
}

