public class Coordinate {
  private int x;
  private int y;

  public Coordinate(int xCoord, int yCoord){
    x = xCoord;
    y = yCoord;
  }

  public int distanceFrom(Coordinate other){
    // TODO: actually calculate distance beteen the coordinates.
    //       e.g. return Math.abs(x - other.x);

    // convert to a double  
    double xDistance = Math.abs(x - other.x);

    // convert to a double, too
    double yDistance = Math.abs(y - other.y);

    // cast as an integer  
    return (int) Math.sqrt(xDistance * xDistance + yDistance * yDistance);
  }
}

