public class Coordinate {
  private int x;
  private int y;

  public Coordinate(int xCoord, int yCoord){
    x = xCoord;
    y = yCoord;
  }

  public int distanceFrom(Coordinate other){    
    int distance = (int)Math.sqrt(Math.pow(Math.abs(other.x-x),2) + Math.pow(Math.abs(other.y-y),2));
      System.out.println("Distance between X:" + this.toString() + "Y:" + other + " is : " + distance);
    return distance;
  }
    public String toString() {
        return "(" + x + "," + y + ")";
    }
}

