import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    private final Shouty shouty = new Shouty();

    @When("^(\\w+) is at (\\d+), (\\d+)$")
    public void user_is_at(String name, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(name, new Coordinate(xCoord, yCoord));
    }

    @When("^(\\w+) shouts$")
    public void shouter_shouts(String name) throws Throwable {
        shouty.shout(name, ARBITRARY_MESSAGE);
    }    

    @Then("^(\\w+) should hear (\\w+)$")
    public void listener_should_hear_shouter(String name, String expectedShouter) throws Throwable {
        if ( expectedShouter != null && expectedShouter.equals("nothing")) {
            Assert.assertEquals(0, shouty.getShoutsHeardBy(name).size());
        } else {
            Assert.assertTrue(shouty.amIhearing(name, expectedShouter));
        }
    }

    @Then("^(\\w+) should not hear (\\w+)$")
    public void listener_should_not_hear_shouter(String name, String expectedShouter) throws Throwable {
        Assert.assertFalse(shouty.amIhearing(name, expectedShouter));
    }    
}

