public class Coordinate 
{
    private int x;
    private int y;

    public Coordinate(int xCoord, int yCoord) {
        x = xCoord;
        y = yCoord;
    }

    public int distanceFrom(Coordinate other) {
        int xDelta = x - other.x;
        int yDelta = y - other.y;
        return (int)Math.sqrt(xDelta*xDelta + yDelta*yDelta);
    }
}

