import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import cucumber.api.DataTable;
import java.util.*;
import org.springframework.beans.factory.annotation.Autowired;

public class ShoutSteps
{
    private final String ARBITRARY_MESSAGE = "Hello, world";

    private final ShoutyHelper shouty = new ShoutyHelper();

    @Given("^(Lucy|Sean|Oscar) is at (\\d+), (\\d+)$")
    public void person_is_at(String name, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(name, new Coordinate(xCoord, yCoord));
    }    

    @When("^(Lucy|Sean|Oscar) shouts$")
    public void person_shouts(String name) throws Throwable {
        shouty.shout(name, ARBITRARY_MESSAGE);
    }

    @Then("^Lucy should hear Sean$")
    public void lucy_should_hear_Sean() throws Throwable {
        assertTrue(heardBy("Lucy").containsKey("Sean"));
    }

    @Then("^Lucy should hear nothing$")
    public void lucy_should_hear_nothing() throws Throwable {
        assertEquals(0, heardBy("Lucy").size());
    }

    @Then("^Lucy should not hear (Oscar|Lucy)$")
    public void lucy_should_not_hear_person(String name) throws Throwable {
        assertFalse(heardBy("Lucy").containsKey(name));
    }

    @Given("^people are located at$")
    public void peopleAreLocatedAt(List<PersonLocation> table) throws Throwable {
        for(PersonLocation row : table)
        {
            shouty.setLocation(row.name, new Coordinate(row.x, row.y));            
        }
    }

    @Then("^Lucy should hear (\\d+) shouts from Sean$")
    public void lucyShouldHearShoutsFromSean(int n) throws Throwable {
        assertEquals(2, heardBy("Lucy").get("Sean").size());
    }    

    private Map<String, List<String>> heardBy(String name) {
        return shouty.getShoutsHeardBy(name);
    }
}

