var {Before, Given, When, Then } = require('cucumber')
var assert = require('assert')
var Shouty = require('../shouty')
var Coordinate = require('../coordinate')

const ARBITARY_MESSAGE = 'Hello, world'
let shouty

Before(function() {
  shouty = new Shouty()
})

Given('{word} is at {int}, {int}', function (name, x, y) {
  shouty.setLocation(name, new Coordinate(x, y))
})

When('Sean shouts', function () {
  shouty.shout('Sean', ARBITARY_MESSAGE)
})

Then('Lucy should hear Sean', function () {
  assert('Sean' in shouty.getShoutsHeardBy('Lucy'))
})

Then('{word} should not hear {word}', function (listener, shouter) {
  assert(!(shouter in shouty.getShoutsHeardBy(listener)))
})

Then('Lucy should hear nothing', function () {
  assert.equal(Object.keys(shouty.getShoutsHeardBy('Lucy')).length, 0)
})

shouty.js:
module.exports = function Shouty() {
  const MESSAGE_RANGE = 1000
  this.locations = {}
  this.shouts = {}

  this.setLocation = function(person, coordinate) {
    this.locations[person] = coordinate
  }

  this.shout = function(person, shout) {
    if(!(person in this.shouts)) {
      this.shouts[person] = []
    }
    this.shouts[person].push(shout)
  }

  this.getShoutsHeardBy = function(listener) {
    var shoutsHeard = {}

    Object.keys(this.shouts).forEach(shouter => {
        var personsShouts = this.shouts[shouter]
        var distance = this.locations[listener].distanceFrom(this.locations[shouter])

        if(distance < MESSAGE_RANGE) {
          shoutsHeard[shouter] = personsShouts
        }
    })

    return shoutsHeard
  }
}

