public class Coordinate {
  private int x;
  private int y;

  public Coordinate(int xCoord, int yCoord){
    x = xCoord;
    y = yCoord;
  }

  public int distanceFrom(Coordinate other){
    // actually calculate distance beteen the coordinates.
    int xDistToZero = calculateDistance(x, y);
    int otherXDistToZero = calculateDistance(other.x, other.y);
    return calculateDistance(xDistToZero, otherXDistToZero);
  }

    public int calculateDistance(int p1, int p2) {
        return (int)Math.sqrt(Math.abs(Math.pow(p1, 2) + Math.pow(p2, 2)));
    }

}

