import org.junit.*;
import java.util.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    private final Shouty shouty = new Shouty();

    @Given("^(\\w+) is at (\\d+), (\\d+)$")
    public void setPersonCoordinate(String person, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(person, new Coordinate(xCoord, yCoord));
    }

    @When("^(\\w+) shouts$")
    public void personShouts(String person) throws Throwable {
        shouty.shout(person, ARBITRARY_MESSAGE);
    }

    @Then("^(\\w+) should hear (\\w+)$")
    public void shoutsAreHeard(String listener, String shouter) throws Throwable {
        Map<String,List<String>> shouts = shouty.getShoutsHeardBy(listener);
        List<String> shoutsByShouter = shouts.get(shouter);
        if(shoutsByShouter == null) 
            shoutsByShouter = new ArrayList<String>();

        if(shouter.equals("nothing")) {
            Assert.assertTrue(shoutsByShouter.size() == 0);
        }
        else {
            Assert.assertTrue(shoutsByShouter.size() > 0);
        }
    }

    @Then("^(\\w+) should not hear (\\w+)$")
    public void shoutIsnotHeard(String listener, String shouter) throws Throwable {
        Map<String,List<String>> shouts = shouty.getShoutsHeardBy(listener);
        Assert.assertTrue(!shouts.containsKey(shouter));
    }
}

