import org.junit.*;
import java.util.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import org.springframework.beans.factory.annotation.Autowired;

public class LocationSteps {

    @Autowired
    private Shouty shouty;

    @Given("people are located at")
    public void setPeopleCoordinates(List<PersonLocation> pLocations) throws Throwable {
        for(PersonLocation pLoc : pLocations) {
            shouty.setLocation(pLoc.getName(), new Coordinate(pLoc.getX(), pLoc.getY()));
        }
    }

    @Given("^(\\w+) is at (\\d+), (\\d+)$")
    public void setPersonCoordinate(String person, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(person, new Coordinate(xCoord, yCoord));
    }
}

