var {defineSupportCode} = require('cucumber')
var assert = require('assert')
var Shouty = require('./shouty')
var Coordinate = require('./coordinate')

defineSupportCode(function({Given, When, Then}) {
  const ARBITARY_MESSAGE = 'Hello, world'


  Given('{word} is at {int}, {int}', function (name, x, y) {
    this.shouty.setLocation(name, new Coordinate(x, y))
  })

  //Given('Sean is at {int}, {int}', function (x, y) {
    //shouty.setLocation('Sean', new Coordinate(x, y))
  //})

  When('{word} shouts', function (shouter) {
    this.shouty.shout(shouter, ARBITARY_MESSAGE)
  })

  Then('{word} should hear {int} shouts from Sean', function (name, shouts) {


    var count = this.shouty.getShoutsHeardBy(name)["Sean"].length;

    assert.equal(count, shouts)
         })

  Then('{word} should hear Sean', function (name) {
    assert("Sean" in this.shouty.getShoutsHeardBy(name))
  })

  Then('{word} should hear nothing', function (name) {
    assert.equal(Object.keys(this.shouty.getShoutsHeardBy(name)).length, 0)
  })

  Then('{word} should not hear Oscar', function (name) {
    assert(!("Oscar" in this.shouty.getShoutsHeardBy(name)))
  })

  Then('{word} should not hear Sean', function (name) {
    assert(!("Sean" in this.shouty.getShoutsHeardBy(name)))
  })

  Given('people are located at', function (dataTable) {
           dataTable.hashes().forEach(row => {
             this.shouty.setLocation(row.name, new Coordinate(row.x, row.y))
           })
         })



})

