module.exports = function Coordinate(x, y) {

  this.x = x
  this.y = y

  this.distanceFrom = function(other) {

    var x1 = Math.abs(this.x - other.x);    
    var y1 = Math.abs(this.y - other.y);
    return Math.hypot(x1 , y1);


    //return 0
  }
};

