var {defineSupportCode} = require('cucumber')
var assert = require('assert')
var Shouty = require('./shouty')
var Coordinate = require('./coordinate')

defineSupportCode(function({Given, When, Then}) {
  const ARBITARY_MESSAGE = 'Hello, world'

  When('{word} shouts', function (name) {
    this.shouty.shout(name, ARBITARY_MESSAGE)
  })

  Then('{word} should hear {word}', function (listener, shouter) {
    if (shouter === "nothing"){
      assert.equal(Object.keys(this.shouty.getShoutsHeardBy(listener)).length, 0)
    } else {
      assert(shouter in this.shouty.getShoutsHeardBy(listener));
    }
  })

  Then('{word} should hear {int} shouts from {word}', function (listener, NumberOfShouts, shouter) {
    var number = 0;
    if (shouter === "nothing"){
      assert.equal(Object.keys(this.shouty.getShoutsHeardBy(listener)).length, 0)
    } else {
      number = this.shouty.getShoutsHeardBy(listener)[shouter].length;
      assert.equal(number, NumberOfShouts);
    }
  })

  Then('{word} should not hear {word}', function (listener, shouter) {
    assert(!(shouter in this.shouty.getShoutsHeardBy(listener)));
  });
})

