module.exports = function Coordinate(x, y) {

  this.x = x
  this.y = y

  this.distanceFrom = function(other) {
    return Math.sqrt(
      Math.pow((other.x - this.x), 2) + 
      Math.pow((other.y - this.y), 2)
    )
  }
};

