var {defineSupportCode} = require('cucumber')
var assert = require('assert')
var Shouty = require('./shouty')
var Coordinate = require('./coordinate')

defineSupportCode(function({Given, When, Then}) {
  const ARBITARY_MESSAGE = 'Hello, world'
  var shouty = new Shouty()

  Given('people are located at', function (dataTable) {
    dataTable.hashes().forEach(row => {
      shouty.setLocation(row.name, new Coordinate(row.x, row.y))
    })
  });

  Given('{word} is at {int}, {int}', function (name, x, y) {
    shouty.setLocation(name, new Coordinate(x, y))
  })

  When('{word} shouts', function (shouter) {
    shouty.shout(shouter, ARBITARY_MESSAGE)
  });

  Then('{word} should hear {word}', function (listener, shouter) {
    if(shouter.toLowerCase() === 'nothing') {
      assert.equal(Object.keys(shouty.getShoutsHeardBy(listener)).length, 0)
    } else {

      assert((shouter in shouty.getShoutsHeardBy(listener)))
    }
  })

  Then('{word} should not hear {word}', function (listener, shouter) {
    assert(!(shouter in shouty.getShoutsHeardBy(listener)))
  })

  Then('{word} should hear {int} shouts from {word}', function (listener, shoutCount, shouter) {
    console.log(shouty.getShoutsHeardBy(listener)[shouter]);
    // { Sean: 
    //   [ 'Hello, world',
    //     'Hello, world',
    //     'Hello, world',
    //     'Hello, world',
    //     'Hello, world'
    //   ]
    // }
    assert(shouty.getShoutsHeardBy(listener)[shouter].length === shoutCount)
  })

})

