  shouty = new Shouty()
})

Given('{string} is at {int}, {int}', function (word, x, y) {
  shouty.setLocation(word, new Coordinate(x, y))
})



When('Sean shouts', function () {
  shouty.shout('Sean', ARBITARY_MESSAGE)
})

