var {Before, Given, When, Then } = require('cucumber')
var assert = require('assert')
var Shouty = require('../shouty')
var Coordinate = require('../coordinate')

const ARBITARY_MESSAGE = 'Hello, world'
let shouty

Before(function() {
  shouty = new Shouty()
})

Given('{word} is at {int}, {int}', function (person,x, y) {
  shouty.setLocation(person, new Coordinate(x, y))
})

When('{word} shouts', function (person) {
  shouty.shout(person, ARBITARY_MESSAGE)
})

Then('Lucy should hear Sean', function () {
  assert.equal(Object.keys(shouty.getShoutsHeardBy('Lucy')).length, 1)
})

Then('{word} should hear nothing', function (person) {
  assert.equal(Object.keys(shouty.getShoutsHeardBy(person)).length, 0)
})

Then('Lucy should not hear Oscar', function () {
  assert.equal(Object.keys(shouty.shoutIsHeardBy('Oscar','Lucy')), false)
})

