var {Before, Given, When, Then } = require('cucumber')
var assert = require('assert')
var Shouty = require('../shouty')
var Coordinate = require('../coordinate')

const ARBITARY_MESSAGE = 'Hello, world'
let shouty

Before(function() {
  shouty = new Shouty()
})

When('{word} shouts', function (shouter) {
  shouty.shout(shouter, ARBITARY_MESSAGE)
})

Then('{word} should hear {word}', function (listener, shouter) {
  assert.equal(Object.keys(shouty.getShoutsHeardBy(listener)).length, 1)
})

Then('{word} should hear nothing at all', function (listener) {
  assert.equal(Object.keys(shouty.getShoutsHeardBy(listener)).length, 0)
})

Then('{word} should not hear {word}', function (listener, shouter) {
  assert.equal(Object.keys(shouty.shoutIsHeardBy(shouter,listener)), false)
})

Then('{word} should hear {int} shouts from {word}', function (listener,num,shouter) {
  assert.equal(shouty.getShoutsHeardBy(listener)[shouter].length, num)
});

