module.exports = function Coordinate(x, y) {

  this.x = x
  this.y = y

  this.distanceFrom = function(other) {

    // To calculate the difference between 2 coordinates we subtract the 
    // passed value from our current value of x to the power of 2
    // Do the same with the Y coordinates
    // We then square root the total of both addded together
   return Math.round(Math.sqrt(Math.pow(this.x - other.x,2) + Math.pow(this.y-other.y,2)))

  }
};

