var {Before, Given, When, Then } = require('cucumber')
var assert = require('assert')
var Shouty = require('../shouty')
var Coordinate = require('../coordinate')

const ARBITARY_MESSAGE = 'Hello, world'
let shouty

Before(function() {
  shouty = new Shouty()
})

Given('{word} is at {int}, {int}', function (n,x, y) {
  shouty.setLocation(n, new Coordinate(x, y))
})

When('{word} shouts', function (name) {
  shouty.shout(name, ARBITARY_MESSAGE)
})

Then('{word} should hear {word}', function (listener, shouter) {
  if (shouter != 'nothing') {
    assert.equal(Object.keys(shouty.getShoutsHeardByShouter(shouter, listener)).length, 1)
  } else{
    if (listener===shouter){
      return 'Pending'
    } else {
      assert.equal(Object.keys(shouty.getShoutsHeardByShouter(shouter, listener)).length, 0)
    }
  }
})

Then('{word} should not hear {word}', function (listener, shouter) {
  assert.equal(Object.keys(shouty.getShoutsHeardByShouter(shouter, listener)).length, 0)
});

