var {Before, Given, When, Then } = require('cucumber')
var assert = require('assert')
var Shouty = require('../shouty')
var Coordinate = require('../coordinate')

const ARBITARY_MESSAGE = 'Hello, world'
let shouty

Before(function() {
  shouty = new Shouty()
})

Given('{word} is at {int}, {int}', function (name, x, y) {
  shouty.setLocation( name, new Coordinate(x, y))
})

When('{word} shouts', function (name) {
  shouty.shout(name, ARBITARY_MESSAGE)
})

Then('{word} should hear {word}', function (shoutee, shouter) {
  if(shouter === 'nothing')
    assert.equal(Object.keys(shouty.getShoutsHeardBy(shoutee)).length, 0)
  else
    assert.equal(Object.keys(shouty.getShoutsHeardBy(shoutee)).includes(shouter), true)
})

Then('{word} should not hear {word}', function (shoutee, shouter) {
  assert.equal(Object.keys(shouty.getShoutsHeardBy(shoutee)).includes(shouter), false)
 });

