coordinate.js:
module.exports = function Coordinate(x, y) {

  this.x = x
  this.y = y

  this.distanceFrom = function(other) {
    var xs = Math.abs(this.x - other.x),
        ys = Math.abs(this.y - other.y)
    xs *= xs
    ys *= ys
    return Math.sqrt(xs + ys)
  }
};

