using System;
using TechTalk.SpecFlow;
using Shouty;
using NUnit.Framework;

namespace Shouty.Specs
{
    [Binding]
    public class ShoutSteps
    {
        private const string ARBITRARY_MESSAGE = "Hello, world";
        private readonly ShoutyNetwork shouty = new ShoutyNetwork();

        [Given(@"(Lucy|Sean|Oscar) is at (.*), (.*)")]
        public void GivenLucyIsAt(string listenerName,int xCoord, int yCoord)
        {
            shouty.SetLocation(listenerName, new Coordinate(xCoord, yCoord));
        }

        [When(@"(Lucy|Sean|Oscar) shouts")]
        public void WhenSeanShouts(string shouterName)
        {
            shouty.Shout(shouterName, ARBITRARY_MESSAGE);
        }

        [Then(@"(Lucy|Sean|Oscar) should hear (Lucy|Sean|Oscar)")]
        public void ThenLucyShouldHearSean(string listenerName, string shouterName)
        {
            Assert.IsTrue(shouty.GetShoutsHeardBy(listenerName).ContainsKey(shouterName));
        }

        [Then(@"(Lucy|Sean|Oscar) should hear nothing")]
        public void ThenLucyShouldHearNothing(string listenerName)
        {
            Assert.AreEqual(0, shouty.GetShoutsHeardBy(listenerName).Count);
        }

        [Then(@"(Lucy|Sean|Oscar) should not hear (Lucy|Sean|Oscar)")]
        public void ThenLucyShouldNotHearOscar(string listenerName, string shouterName)
        {
            Assert.IsFalse(shouty.GetShoutsHeardBy(listenerName).ContainsKey(shouterName));
        }
    }
}

