using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace Shouty
{
    public class Coordinate
    {
        private readonly int xCoord;
        private readonly int yCoord;

        public Coordinate(int xCoord, int yCoord)
        {
            this.xCoord = xCoord;
            this.yCoord = yCoord;
        }

        public int DistanceFrom(Coordinate other)
        {
            var xDistance = Math.Abs(xCoord - other.xCoord);
            var yDistance = Math.Abs(yCoord - other.yCoord);
            var totalDistance = Math.Sqrt(xDistance * xDistance + yDistance * yDistance);
            return (int)totalDistance;
        }
    }
}

