import cucumber.api.java.en.Given;
import cucumber.api.java.en.Then;
import cucumber.api.java.en.When;
import org.springframework.beans.factory.annotation.Autowired;

import static java.util.Collections.emptyMap;
import static org.junit.Assert.assertEquals;

public class ShoutSteps {

    @Autowired
    private Shouty shouty;

    private static final String ARBITRARY_MESSAGE = "Hello, world";

    @When("{word} shouts")
    public void shouts(String shouter) {
        shouty.shout(shouter, ARBITRARY_MESSAGE);
    }

    @Then("{word} should hear {word}")
    public void listner_should_hear_shouter(String listner, String shouter) {
        assertEquals(1, shouty.getShoutsHeardByFrom(listner,shouter));
    }

    @Then("{word} should hear 'nothing'")
    public void listner_should_hear_nothing(String listner) {
        assertEquals(emptyMap(), shouty.getShoutsHeardBy(listner));
    }
    @Then("{word} should not hear {word}")
    public void listner_should_not_hear_shouter(String listner, String shouter) {
        assertEquals(0, shouty.getShoutsHeardByFrom(listner,shouter));
    }

     @Then("{word} should hear {int} shout(s) from {word}")
    public void listner_should_hear_shouter(String listner, int countShouts, String shouter) {
        assertEquals(countShouts, shouty.getShoutsHeardByFrom(listner,shouter));
    }
}

