import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import java.util.*;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    private final Shouty shouty = new Shouty();

    @Given("^(.+) is at (\\d+), (\\d+)$")
    public void personIsAt(String name, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(name, new Coordinate(xCoord, yCoord));
    }

    @When("^(.+) shouts$")
    public void seanShouts(String name) throws Throwable {
        shouty.shout(name, ARBITRARY_MESSAGE);
    }

    @Then("^(\\w+) should hear (\\d+) shouts from (\\w+)$")
    public void multipleShouts(String listener, int numberShouts, String shouter) {
        Assert.assertEquals(numberShouts, shouty.getShoutsHeardBy(listener).containsKey(shouter).size());
    }

    @Then("^(\\w+) should hear (\\w+)$")
    public void listenerShouldHearShouter(String listener, String shouter) throws Throwable {
        Assert.assertEquals(true, shouty.getShoutsHeardBy(listener).containsKey(shouter));
    }

    @Then("^(.+) should not hear (.+)$")
    public void listenerShouldNotHearShouter(String listener, String shouter) throws Throwable {
    // Write code here that turns the phrase above into concrete actions
        switch(shouter) {
            case "anything":
                Assert.assertEquals(0, shouty.getShoutsHeardBy(listener).size());
                break;
            default:
                Assert.assertEquals(false, shouty.getShoutsHeardBy(listener).containsKey(shouter));
                break;
        }

    }

    @Given("^people are located at$")
    public void peopleAreLocatedAt(List<PersonLocation> people) throws Throwable {
        for (PersonLocation p: people) {
            shouty.setLocation(p.getName(), new Coordinate(p.getX(), p.getY()));
        }
    }
}

