import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import cucumber.api.DataTable;
import java.util.List;
import org.springframework.beans.factory.annotation.Autowired;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";

    @Autowired
    private ShoutyHelper shouty;


    @When("^(\\w+) shouts$")
    public void shouterShouts(String name) throws Throwable {
        shouty.shout(name, ARBITRARY_MESSAGE);
    }

    @Then("^(\\w+) should hear (\\w+)$")
    public void listenerShouldHearShouter(String listener, String shouter) throws Throwable {
        Assert.assertNotNull(shouty.getShoutsHeardBy(listener).get(shouter));
        Assert.assertEquals(1, shouty.getShoutsHeardBy(listener).get(shouter).size());
    }

    @Then("^(\\w+) should not hear anything$")
    public void listenerShouldHearNothing(String listener) throws Throwable {
        Assert.assertEquals(0, shouty.getShoutsHeardBy(listener).size());
    }

    @Then("^(\\w+) should not hear shout from (\\w+)$")
    public void listenerShouldNotHearShouter(String listener, String shouter) throws Throwable {
        Assert.assertNull(shouty.getShoutsHeardBy(listener).get(shouter));
    }


    @Then("^(\\w+) should hear (\\d+) shouts from (\\w+)$")
    public void lucyShouldHearShoutsFromSean(String listener,int shoutCount, String shouter) throws Throwable {
        Assert.assertNotNull(shouty.getShoutsHeardBy(listener).get(shouter));
        Assert.assertEquals(shoutCount,shouty.getShoutsHeardBy(listener).get(shouter).size());
    }
}

