import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import java.util.*;
import org.springframework.beans.factory.annotation.*;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    @Autowired
    private ShoutyHelper shouty;

    @When("^(.*) shouts$")
    public void personShouts(String name) throws Throwable {
        personShouts(name, ARBITRARY_MESSAGE);
    }

    @When("^(.*) sends shout (.*)$")
    public void personShouts(String name, String message) throws Throwable {
        shouty.shout(name, message);
    }

    @Then("^Lucy should hear Sean$")
    public void lucyShouldHearHim() throws Throwable {
        lucyShouldHearShoutsFromSean(1);
    }

    @Then("^Lucy should hear (\\d+) shouts from Sean$")
    public void lucyShouldHearShoutsFromSean(int shouts) throws Throwable {
        Assert.assertEquals(true, shouty.getShoutsHeardBy("Lucy").containsKey("Sean"));
        Assert.assertEquals(shouts, shouty.getShoutsHeardBy("Lucy").get("Sean").size());
    }

    @Then("^Lucy should not hear (Lucy|Oscar)$")
    public void lucyShouldNotHearPerson(String name) throws Throwable {
         Assert.assertEquals(false, shouty.getShoutsHeardBy("Lucy").containsKey(name));
    }

    @Then("^Lucy should hear nothing$")
    public void lucy_should_hear_nothing() throws Throwable {
        Assert.assertEquals(0, shouty.getShoutsHeardBy("Lucy").size());
    }



}

Shouty.java:
import java.util.*;
import org.springframework.stereotype.*;
import org.springframework.context.annotation.*;
import cucumber.api.java.en.*;


public class Shouty {
  private static final int MESSAGE_RANGE = 1000;
  private Map<String, Coordinate> locations = new HashMap<String, Coordinate>();
  private Map<String, List<String> > shouts = new HashMap<String, List<String> >();
  private Map<String, List<String> > blockedHashtags = new HashMap<String, List<String> >();
  private Map<String, Boolean> dndToggle = new HashMap<String, Boolean>();

  public void setLocation(String person, Coordinate location) {
      locations.put(person, location);
  }

  public void shout(String shouter, String shout) {
      if (!shouts.containsKey(shouter)) {
          List<String> personsShouts = new ArrayList<String>();
          shouts.put(shouter, personsShouts);
      }

      shouts.get(shouter).add(shout);
  }

  public Map<String, List<String> > getShoutsHeardBy(String listener) {
      HashMap<String, List<String> > shoutsHeard = new HashMap<String, List<String> >();
      if (!isDnDOn(listener)) {
          for (Map.Entry<String, List<String> > entry : shouts.entrySet()) {
              String shouter = entry.getKey();
              if (!listener.equals(shouter)) { 

                List<String> personsShouts = filterShouts(listener, entry.getValue());
                int distance = locations.get(listener).distanceFrom(locations.get(shouter));
                if (distance < MESSAGE_RANGE)
                {
                    shoutsHeard.put(shouter, personsShouts);
                }

              }
          }
      }
      return shoutsHeard;
  }

  public void blockHashTag(String user, String hashtag) {
    List<String> hashTags;

    if (blockedHashtags.containsKey(hashtag)) {
        hashTags = blockedHashtags.get(user);   
    } else {
        hashTags = new ArrayList<String>(); 
    }

    hashTags.add(hashtag);
  }

  public void unblockHashTag(String user, String hashtag) {
    if (blockedHashtags.containsKey(user)) {
        List<String> hashTags = blockedHashtags.get(user); 
        hashTags.remove(hashtag);
    }
  }

  public void setDnD(String user, Boolean value) {
      dndToggle.put(user, value);
  }

  public Boolean isDnDOn(String user) {
    if (dndToggle.containsKey(user)) {
        return dndToggle.get(user);
    }
    return false;
  }

  public List<String> filterShouts(String reciever, List<String> shouts) {
    List<String> filtered = new ArrayList<String>();
    for (String shout : shouts) {
        if (!hashtagBlocked(reciever, shout)) {
            filtered.add(shout);
        }
    }

    return filtered;
  }

  public Boolean hashtagBlocked(String user, String message) {
    if (blockedHashtags.containsKey(user)) {
        List<String> hashTags = blockedHashtags.get(user); 

        for (String hashtag : hashTags) {
            if (message.contains(hashtag)) {
                return true;
            }
        }
    }

    return false;
  }



}

