import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import java.util.*;
import org.springframework.beans.factory.annotation.*;

public class DnDSteps {
    @Autowired
    private ShoutyHelper shouty;

    @Given("^Lucy has enabled DND$")
    public void lucyHasEnabledDND() throws Throwable {
        shouty.setDnD("Lucy", true);
    }

    @Given("^Lucy has disabled DND$")
    public void lucyHasDisabledDND() throws Throwable {
        shouty.setDnD("Lucy", false);
    }

    @Given("^Lucy has specified \"([^\"]*)\"$")
    public void lucyHasSpecified(String hashtag) throws Throwable {
        shouty.blockHashTag("Lucy", hashtag);
    }

    @When("^Lucy has unblocked \"([^\"]*)\"$")
    public void lucyHasUnspecified(String hashtag) throws Throwable {
        shouty.unblockHashTag("Lucy", hashtag);
    }

    @Then("^Lucy should only see \"([^\"]*)\" from Oscar$")
    public void lucyShouldOnlySeeFromOscar(String hashtag) throws Throwable {
        Assert.assertEquals(true, shouty.getShoutsHeardBy("Lucy").containsKey("Oscar"));
        Assert.assertEquals(1, shouty.getShoutsHeardBy("Lucy").get("Oscar").size());
    }


}
