import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import java.util.*;
import org.springframework.beans.factory.annotation.*;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    @Autowired
    private Shouty shouty;

    @When("^(.*) shouts$")
    public void seanShouts(String name) throws Throwable {
        shouty.shout(name, ARBITRARY_MESSAGE);
    }

    @Then("^Lucy should hear Sean$")
    public void lucyShouldHearHim() throws Throwable {
        lucyShouldHearShoutsFromSean(1);
    }

    @Then("^Lucy should hear (\\d+) shouts from Sean$")
    public void lucyShouldHearShoutsFromSean(int shouts) throws Throwable {
        Assert.assertEquals(true, shouty.getShoutsHeardBy("Lucy").containsKey("Sean"));
        Assert.assertEquals(shouts, shouty.getShoutsHeardBy("Lucy").get("Sean").size());
    }

    @Then("^Lucy should not hear (Lucy|Oscar)$")
    public void lucyShouldNotHearPerson(String name) throws Throwable {
         Assert.assertEquals(false, shouty.getShoutsHeardBy("Lucy").containsKey(name));
    }

    @Then("^Lucy should hear nothing$")
    public void lucy_should_hear_nothing() throws Throwable {
        Assert.assertEquals(0, shouty.getShoutsHeardBy("Lucy").size());
    }
}

