import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import java.util.*;
import org.springframework.beans.factory.annotation.*;

public class LocationSteps {
    @Autowired
    private Shouty shouty;

    @Given("^people are locate at$")
    public void peopleAreLocateAt(List<PersonLocation> locations) throws Throwable {
        for (PersonLocation location : locations) {
            shouty.setLocation(location.name, new Coordinate(location.x, location.y));
        }
    }

    @Given("^(.*) is at (\\d+), (\\d+)$")
    public void personIsAt(String name, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(name, new Coordinate(xCoord, yCoord));
    }
}
