import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import java.util.*;
import org.springframework.beans.factory.annotation.Autowired;

public class ShoutSteps {

    private final String ARBITRARY_MESSAGE = "Hello, world";

    @Autowired
    private ShoutyHelper shouty;

    @When("^(Sean|Oscar) shouts$")
    public void personShouts(String name) throws Throwable {
        shouty.shout(name, ARBITRARY_MESSAGE);
    }

    @Then("^Lucy should hear Sean$")
    public void lucyShouldHearHim() throws Throwable {
        Assert.assertEquals(1, shouty.getShoutsHeardBy("Lucy").size());
    }

    @Then("^Lucy should not hear Oscar$")
    public void lucyShouldNotHearHim() throws Throwable {
        Assert.assertEquals(1, shouty.getShoutsHeardBy("Lucy").size());
    }

    @Then("^(Lucy|Sean) should hear nothing$")
    public void lucy_should_hear_nothing(String name) throws Throwable {
        Assert.assertEquals(0, shouty.getShoutsHeardBy(name).size());
    }

    @Then("^Lucy should hear 2 shouts from Sean$")
    public void lucyShouldHear2ShoutsFromSean() {
        Assert.assertEquals(2, shouty.getShoutsHeardBy("Lucy").get("Sean").size());
    }


}

