import java.util.*;

import org.springframework.beans.factory.annotation.Autowired;

import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";

    @Autowired
    private Shouty shouty;

    @When("^(\\w+) shouts$")
    public void userShouts(String name) throws Throwable {
        shouty.shout(name, ARBITRARY_MESSAGE);
    }

    @Then("^Lucy should hear Sean$")
    public void lucyShouldHearHim() throws Throwable {
        Assert.assertEquals(1, shouty.getShoutsHeardBy("Lucy").get("Sean").size());
    }

    @Then("^Lucy should hear nothing$")
    public void lucy_should_hear_nothing() throws Throwable {
        Assert.assertEquals(0, shouty.getShoutsHeardBy("Lucy").size());
    }

    @Then("^Lucy should not hear Oscar$")
    public void lucyShouldNotHearOscar() throws Throwable {
        Assert.assertEquals(null, shouty.getShoutsHeardBy("Lucy").get("Oscar"));
    }

    @Then("^(\\w+) should not hear herself$")
    public void userShouldNotHearHerself(String name) throws Throwable {
        Assert.assertEquals(0, shouty.getShoutsHeardBy(name).size());
    }

    @Then("^Lucy should hear (\\d+) shouts from Sean$")
    public void lucyShouldHearShoutsFromSean(int numShouts) throws Throwable {
        Assert.assertEquals(numShouts, shouty.getShoutsHeardBy("Lucy").get("Sean").size());
    }
}

