import java.util.*;

import org.springframework.beans.factory.annotation.Autowired;

import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;

public class LocationSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";

    @Autowired
    private Shouty shouty;

    @Given("^people are located at$")
    public void peopleAreLocatedAt(List<PersonLocation> personLocation) throws Throwable {
        for(PersonLocation pl : personLocation) {
            shouty.setLocation(pl.name, new Coordinate(pl.x, pl.y));
        }
    }

    @Given("^(\\w+) is at (\\d+), (\\d+)$")
    public void userIsAt(String name, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(name, new Coordinate(xCoord, yCoord));
    }
}

