public class Coordinate {
  private int x;
  private int y;

  public Coordinate(int xCoord, int yCoord){
    x = xCoord;
    y = yCoord;
  }

  public int distanceFrom(Coordinate other){

      int xAbs = Math.abs(x - other.x);
      int yAbs = Math.abs(y - other.y);

      return (int) Math.sqrt(Math.pow(xAbs, 2)+ Math.pow(yAbs, 2));

  }
}

