import org.junit.*;
import java.util.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.context.annotation.Scope;

public class ShoutSteps {
    private final static String ARBITRARY_MESSAGE = "Hello, world";
    private final static Coordinate ARBITRARY_LOCATION = new Coordinate(0, 0);
    @Autowired
    @Scope("cucumber-glue")
    private Shouty shouty;

    @When("^(\\w+) shouts$")
    public void shouterShouts(String shouter) {
        shouty.shout(shouter, ARBITRARY_MESSAGE);
    }

    @Then("^(\\w+) should hear (\\d+) shout[s]* from (\\w+)$")
    public void receiverShouldHearShouter(String receiver, int shoutCount, String shouter){
        Map<String, List<String>> shoutsHeard = shouty.getShoutsHeardBy(receiver);
        Assert.assertEquals(shoutCount, shoutsHeard.get(shouter).size());
    }

    @Then("^(\\w+) should hear nothing$")
    public void receiverShouldHearNothing(String receiver) {
        Assert.assertEquals(0, shouty.getShoutsHeardBy(receiver).size());
    }

    @Then("^(\\w+) should not hear (\\w+)$")
    public void receiverShouldNotHearShouter(String receiver, String shouter) {
        Assert.assertFalse(shouty.getShoutsHeardBy(receiver).containsKey(shouter));
    }

}

