import org.junit.*;
import java.util.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.context.annotation.Scope;

public class LocationSteps{
    private final static Coordinate ARBITRARY_LOCATION = new Coordinate(0, 0);
    @Autowired
    @Scope("cucumber-glue")
    private Shouty shouty;

    @Given("^(\\w+) is at (\\d+), (\\d+)$")
    public void personIsAt(String person, int xCoord, int yCoord) {
        shouty.setLocation(person, new Coordinate(xCoord, yCoord));
    }

    @Given("^people are located at$")
    public void peopleAreLocatedAt(List<PersonLocation> table) {
        for(PersonLocation personLocation : table){
           String person = personLocation.getName();
           int xCoord = personLocation.getX();
           int yCoord = personLocation.getY();
           shouty.setLocation(person, new Coordinate(xCoord, yCoord));
        }
    }

    @Given("^(\\w+) is anywhere$")
    public void personIsAt(String person) {
        shouty.setLocation(person, ARBITRARY_LOCATION);
    }
}
