import org.junit.*;
import static org.junit.Assert.*;
import cucumber.api.java.en.*;
import cucumber.api.PendingException;

public class ShoutSteps {
    private final String ARBITRARY_MESSAGE = "Hello, world";
    private final Shouty shouty = new Shouty();

    @Given("^(Lucy|Sean|Oscar) is at (\\d+), (\\d+)$")
    public void personIsAt(String person, int xCoord, int yCoord) throws Throwable {
        shouty.setLocation(person, new Coordinate(xCoord, yCoord));
    }

    @When("^(Oscar|Sean|Lucy) shouts$")
    public void personShouts(String names) throws Throwable {
        shouty.shout(names, ARBITRARY_MESSAGE);
    }

    @Then("^Lucy should hear nothing$")
    public void lucyShouldHearNothing() throws Throwable {
        Assert.assertEquals(0, shouty.getShoutsHeardBy("Lucy").size());
    }

    @Then("^Lucy should not hear Oscar$")
    public void lucyShouldNotHearOscar() throws Throwable {
        Assert.assertFalse(shouty.getShoutsHeardBy("Lucy").containsKey("Oscar"));
    }

    @Then("^Lucy should hear Sean$")
    public void lucyShouldHearSean() throws Throwable {
        Assert.assertTrue(shouty.getShoutsHeardBy("Lucy").containsKey("Sean"));
    }

    @Then("^Lucy should not hear herself$")
    public void lucyShouldNotHearHerself() throws Throwable {
        Assert.assertFalse(shouty.getShoutsHeardBy("Lucy").containsKey("Lucy"));
    }

    @Then("^Lucy should hear (\\d+) shouts from Sean$")
    public void lucyShouldHearTwoShoutsFromSean() throws Throwable {

        Assert.assertTrue(shouty.getShoutsHeardBy("Lucy").get("Sean").count());
    }

}

