public class Coordinate {
  private int x;
  private int y;

  public Coordinate(int xCoord, int yCoord){
    x = xCoord;
    y = yCoord;
  }

  private int getDelta(int a, int b){
      return Math.abs(a - b);
  }

  public int distanceFrom(Coordinate other){

    int xDist = getDelta(x, other.x);
    int yDist = getDelta(y, other.y);

    return (int) Math.hypot(xDist, yDist);
  }
}

