public class Coordinate {
  private final int x;
  private final int y;

  public Coordinate(int xCoord, int yCoord){
    x = xCoord;
    y = yCoord;
  }

  public int distanceFrom(Coordinate other){
      int a = x - other.x;
      int b = y - other.y;
      return (int)Math.sqrt(a * a + b * b);
  }
}

